import { css } from "styled-components";
import { POSITIONS, POPOVER_SPACE_BETWEEN } from "../consts";

const resolvePopoverPosition = ({
  position,
  containerTop,
  containerHeight,
  popoverHeight
}) => {
  if (position === POSITIONS.TOP) {
    return css(["top:", "px;"], Math.floor(containerTop - popoverHeight - POPOVER_SPACE_BETWEEN));
  }

  if (position === POSITIONS.BOTTOM) {
    return css(["top:", "px;"], Math.floor(containerTop + containerHeight + POPOVER_SPACE_BETWEEN));
  }

  return null;
};

export default resolvePopoverPosition;