import { css } from "styled-components";
import { isPositionBottom, isPositionLeft, isPositionRight, isPositionTop } from "./isPosition";
import { TOOLTIP_ARROW_SIZE } from "../consts";

const resolveTooltipArrowPosition = ({
  position
}) => {
  const cssPosition = isPositionTop(position) && "bottom" || isPositionBottom(position) && "top" || isPositionLeft(position) && "right" || isPositionRight(position) && "left";
  return css(["", ":-", "px;"], cssPosition, parseFloat(TOOLTIP_ARROW_SIZE));
};

export default resolveTooltipArrowPosition;